    e.code ==="Enter" && handleSearch(); // if enter just search for user
  }

  const handleSelect = async() => {


    //create user chats 

    const combinedId = currentUser.uid > user.uid  
    ? currentUser.uid + user.uid 
    : user.id+currentUser.uid;

